(()=>{
document.querySelectorAll('[data-gradient]').forEach(el=>{
const c=el.dataset.gradient.split(' ');
el.style.background=`linear-gradient(120deg,${c.join(',')})`;
el.style.backgroundSize='400% 400%';
el.animate([{backgroundPosition:'0% 50%'},{backgroundPosition:'100% 50%'}],{duration:12000,iterations:Infinity});
});
})();
